const spanInput = document.querySelectorAll(".spanInput");
function loaded() {
  for (let i = 0; i < spanInput.length; i++) {
    spanInput[i].classList.add("transition", "delay-700", "duration-300" ,"ease-in-out");
    spanInput[i].classList.remove("left-3", "top-[14px]");
    spanInput[i].classList.add("bg-white");
  }
}
contentProductDetail.addEventListener('onload',loaded())
function rippleEffect(event) {
  const btn = event.currentTarget;

  const circle = document.createElement("span");
  const diameter = Math.max(btn.clientWidth, btn.clientHeight);
  const radius = diameter / 2;
  circle.classList.add("z-10");
  circle.style.width = circle.style.height = `${diameter}px`;
  circle.style.left = `${event.clientX - (btn.offsetLeft + radius)}px`;
  circle.style.top = `${event.clientY - (btn.offsetTop + radius)}px`;
  circle.classList.add("ripple");

  const ripple = btn.getElementsByClassName("ripple")[0];

  if (ripple) {
    ripple.remove();
  }

  btn.appendChild(circle);
}

const btn = document.querySelectorAll(".bt");
for (let i = 0; i < btn.length; i++) {
  btn[i].addEventListener("click", rippleEffect);
}
//slide size
const trentabtn = document.querySelector("#trentaBtn");
const trentaIcon = document.querySelector("#trentaIcon");

const ventiBtn = document.querySelector("#ventiBtn");
const ventiIcon = document.querySelector("#ventiIcon");

const gradeBtn = document.querySelector("#gradeBtn");
const gradeIcon = document.querySelector("#gradeIcon");

const tallBtn = document.querySelector("#tallBtn");
const tallIcon = document.querySelector("#tallIcon");

const textCalories = document.querySelector("#textCalories");

const discreaseBtn = document.querySelector("#DiscreaseBtn");
const increaseBtn = document.querySelector("#increaseBtn");
const numberOfAddIn = document.querySelector("#numberOfAddIn");
let number = 1;

slideSizeBg(trentabtn, trentaIcon, "4px", 380);
slideSizeBg(ventiBtn, ventiIcon, "68px", 280);
slideSizeBg(gradeBtn, gradeIcon, "135px", 200);
slideSizeBg(tallBtn, tallIcon, "201px", 150);

function slideSizeBg(btn, icon, right, calories) {
  const sizeBg = document.querySelector("#sizeBg");
  const listOfIcon = [trentaIcon, ventiIcon, gradeIcon, tallIcon];

  btn.addEventListener("click", function () {
    for (let i = 0; i < listOfIcon.length; i++) {
      if (listOfIcon[i] != icon) {
        listOfIcon[i].classList.remove("opacity-100");
      }
    }
    if (btn == trentabtn) {
      number = 2;
      numberOfAddIn.innerHTML = number;
    } else {
      number = 1;
      numberOfAddIn.innerHTML = number;
    }
    sizeBg.style.right = right;
    icon.classList.add("opacity-100");
    textCalories.innerHTML = calories;
  });
}
discreaseBtn.addEventListener("click", function () {
  if (number <= 0) {
    const textAddIn = document.querySelector("#addInText");
    textAddIn.innerHTML = noAddInText;
    numberOfAddIn.classList.add("hidden");
    discreaseBtn.classList.add("hidden");
  } else {
    number--;
    numberOfAddIn.innerHTML = number;
  }
});
increaseBtn.addEventListener("click", function () {
  if (number >= 0) {
    const textAddIn = document.querySelector("#addInText");
    textAddIn.innerHTML = addInText;
    number++;
    numberOfAddIn.classList.remove("hidden");
    discreaseBtn.classList.remove("hidden");
    numberOfAddIn.innerHTML = number;
  }
});
